import type { Express, RequestHandler } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPrayerRecordSchema, insertAchievementSchema, dateParamSchema, dateRangeQuerySchema, userStatsUpdateSchema, batchUpdatePrayersSchema } from "@shared/schema";
interface DevAuth {
  (req: any, res: any, next: any): void;
}

// Development middleware that provides a demo user when not using authentication
const devAuth: DevAuth = (req: any, res, next) => {
  req.user = demoUser;
  next();
};

// Import z here
import { z } from "zod";

// Helper function to calculate and update user statistics
async function updateUserStatistics(userId: string): Promise<void> {
  try {
    // Get all prayer records for the user
    const allRecords = await storage.getPrayerRecords(userId);

    let totalPrayers = 0;
    let onTimePrayers = 0;
    let qazaPrayers = 0;
    let currentStreak = 0;
    let bestStreak = 0;
    let perfectWeeks = 0;

    // Sort records by date for streak calculation
    const sortedRecords = allRecords.sort((a, b) => b.date.localeCompare(a.date));

    // Calculate current streak (from most recent date backwards)
    let streakBroken = false;
    for (const record of sortedRecords) {
      if (!streakBroken && record.prayers) {
        const dayPrayers = Object.values(record.prayers);
        const allCompleted = dayPrayers.every(prayer => prayer.completed);

        if (allCompleted) {
          currentStreak++;
        } else {
          streakBroken = true;
        }
      }
    }

    // Calculate historical streaks to find best streak
    let tempStreak = 0;
    const chronologicalRecords = allRecords.sort((a, b) => a.date.localeCompare(b.date));

    for (const record of chronologicalRecords) {
      if (record.prayers) {
        const dayPrayers = Object.values(record.prayers);

        // Count prayers for totals
        dayPrayers.forEach(prayer => {
          totalPrayers++;
          if (prayer.completed) {
            if (prayer.onTime) {
              onTimePrayers++;
            }
          } else {
            qazaPrayers++;
          }
        });

        // Check streak
        const allCompleted = dayPrayers.every(prayer => prayer.completed);
        if (allCompleted) {
          tempStreak++;
          bestStreak = Math.max(bestStreak, tempStreak);
        } else {
          tempStreak = 0;
        }
      }
    }

    // Calculate perfect weeks
    const weekGroups = groupRecordsByWeek(chronologicalRecords);
    perfectWeeks = weekGroups.filter(week => week.completionRate === 100).length;

    // Ensure best streak includes current streak
    bestStreak = Math.max(bestStreak, currentStreak);

    // Update or create user statistics
    let userStats = await storage.getUserStats(userId);

    if (userStats) {
      await storage.updateUserStats(userId, {
        totalPrayers: totalPrayers - qazaPrayers, // Only count completed prayers
        onTimePrayers,
        qazaPrayers,
        currentStreak,
        bestStreak,
        perfectWeeks,
        lastStreakUpdate: new Date().toISOString().split('T')[0],
        updatedAt: new Date(),
      });
    } else {
      await storage.createUserStats({
        userId,
        totalPrayers: totalPrayers - qazaPrayers,
        onTimePrayers,
        qazaPrayers,
        currentStreak,
        bestStreak,
        perfectWeeks,
        lastStreakUpdate: new Date().toISOString().split('T')[0],
      });
    }
  } catch (error) {
    console.error('Failed to update user statistics:', error);
  }
}

// Helper function to group records by week
function groupRecordsByWeek(records: any[]): Array<{ completionRate: number }> {
  const weeks: { [key: string]: { completed: number; total: number } } = {};

  records.forEach(record => {
    const date = new Date(record.date);
    const weekStart = new Date(date);
    weekStart.setDate(date.getDate() - (date.getDay() === 0 ? 6 : date.getDay() - 1));
    const weekKey = weekStart.toISOString().split('T')[0];

    if (!weeks[weekKey]) {
      weeks[weekKey] = { completed: 0, total: 0 };
    }

    if (record.prayers) {
      Object.values(record.prayers).forEach((prayer: any) => {
        weeks[weekKey].total++;
        if (prayer.completed) {
          weeks[weekKey].completed++;
        }
      });
    }
  });

  return Object.values(weeks)
    .filter(week => week.total >= 35) // Only count complete weeks (7 days * 5 prayers)
    .map(week => ({
      completionRate: week.total > 0 ? Math.round((week.completed / week.total) * 100) : 0
    }));
}

// Create a fake user for demo purposes when not using authentication
const demoUser = { userId: "demo-user", username: "Demo User", email: "demo@example.com" };

export async function registerRoutes(app: Express): Promise<Server> {
  console.log("Authentication disabled - using demo user");

  // Import custom auth functions - COMMENTED OUT
  // const { register, login, getMe, authenticate } = await import("./auth");
  // Use devAuth middleware instead
  const isAuthenticated = devAuth;

  // Custom Authentication Routes - COMMENTED OUT
  // app.post('/api/auth/register', register);
  // app.post('/api/auth/login', login);
  // app.get('/api/auth/me', isAuthenticated, getMe);

  // Auth routes - COMMENTED OUT
  // app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
  //   try {
  //     const userId = req.user.userId;
  //     const user = await storage.getUser(userId);
  //     res.json(user);
  //   } catch (error) {
  //     console.error("Error fetching user:", error);
  //     res.status(500).json({ message: "Failed to fetch user" });
  //   }
  // });

  // Get prayer record for a specific date
  app.get("/api/prayers/:date", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Validate date parameter
      const validatedParams = dateParamSchema.parse(req.params);
      const { date } = validatedParams;

      // Add cache control headers to prevent 304 responses
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const record = await storage.getPrayerRecord(userId, date);
      res.json(record || null);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid date parameter', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(500).json({ message: errorMessage });
    }
  });

  // Update prayer record
  app.post("/api/prayers", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;
      const validatedData = insertPrayerRecordSchema.parse({ ...req.body, userId });

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const record = await storage.updatePrayerRecord(
        validatedData.userId!,
        validatedData.date,
        validatedData.prayers as any
      );

      // Automatically update user statistics after saving prayer record
      await updateUserStatistics(userId);

      res.json(record);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid prayer record data', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Validation error';
      res.status(400).json({ message: errorMessage });
    }
  });

  // Batch update prayer records
  app.post("/api/prayers/batch", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Validate batch update request
      const validatedData = batchUpdatePrayersSchema.parse(req.body);

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      // Perform batch update
      const updatedRecords = await storage.batchUpdatePrayerRecords(userId, validatedData.updates);

      // Automatically update user statistics after batch update
      await updateUserStatistics(userId);

      res.json(updatedRecords);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid batch update data', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Validation error';
      res.status(400).json({ message: errorMessage });
    }
  });

  // Get prayer records for a date range
  app.get("/api/prayers", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Validate query parameters
      const validatedQuery = dateRangeQuerySchema.parse(req.query);
      const { startDate, endDate } = validatedQuery;

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const records = await storage.getPrayerRecords(
        userId,
        startDate,
        endDate
      );

      res.json(records);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid query parameters', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(500).json({ message: errorMessage });
    }
  });

  // Get user achievements
  app.get("/api/achievements", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const achievements = await storage.getAchievements(userId);
      res.json(achievements);
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(500).json({ message: errorMessage });
    }
  });

  // Create achievement
  app.post("/api/achievements", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;
      const validatedData = insertAchievementSchema.parse({ ...req.body, userId });

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const achievement = await storage.createAchievement(validatedData);
      res.json(achievement);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid achievement data', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Validation error';
      res.status(400).json({ message: errorMessage });
    }
  });

  // Get user statistics
  app.get("/api/stats", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;
      let stats = await storage.getUserStats(userId);

      if (!stats) {
        // Create initial stats if they don't exist
        stats = await storage.createUserStats({ userId });
      }

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      res.json(stats);
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(500).json({ message: errorMessage });
    }
  });

  // Update user statistics
  app.patch("/api/stats", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Validate request body
      const validatedUpdates = userStatsUpdateSchema.parse(req.body);

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      const stats = await storage.updateUserStats(userId, validatedUpdates);
      res.json(stats);
    } catch (error: unknown) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: 'Invalid user statistics data', 
          errors: error.errors.map(e => e.message) 
        });
      }
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(400).json({ message: errorMessage });
    }
  });

  // Get yearly Qaza statistics
  app.get("/api/stats/yearly-qaza", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Add cache control headers
      res.set({
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'
      });

      // Calculate yearly Qaza statistics
      const today = new Date();
      const currentYear = today.getFullYear();
      const startOfYear = new Date(currentYear, 0, 1);

      // Calculate total days from start of year to today (inclusive)
      const daysSinceStartOfYear = Math.floor((today.getTime() - startOfYear.getTime()) / (1000 * 60 * 60 * 24)) + 1;

      // Total possible prayers for the year so far (5 prayers per day)
      const totalPossible = daysSinceStartOfYear * 5;

      // Get year dates up to today
      const yearDates: string[] = [];
      for (let d = new Date(startOfYear); d <= today; d.setDate(d.getDate() + 1)) {
        yearDates.push(d.toISOString().split('T')[0]);
      }

      const startDate = yearDates[0];
      const endDate = yearDates[yearDates.length - 1];

      const records = await storage.getPrayerRecords(userId, startDate, endDate);

      let completed = 0;

      yearDates.forEach(date => {
        const record = records.find(r => r.date === date);
        if (record && record.prayers) {
          Object.values(record.prayers).forEach(prayer => {
            if (prayer.completed) completed++;
          });
        }
      });

      const qazaRemaining = Math.max(0, totalPossible - completed);

      const response = {
        totalPossible,
        completed,
        qazaRemaining,
        currentYear
      };

      res.json(response);
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      console.error('Error calculating yearly Qaza stats:', error);
      res.status(500).json({ message: errorMessage });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  prayerRecords,
  achievements,
  userStats,
  type User,
  type UpsertUser,
  type PrayerRecord,
  type InsertPrayerRecord,
  type Achievement,
  type InsertAchievement,
  type UserStats,
  type InsertUserStats,
  type DailyPrayers,
  type BatchUpdatePrayers,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, gte, lte } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  // User management
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Prayer records
  getPrayerRecord(userId: string, date: string): Promise<PrayerRecord | undefined>;
  getPrayerRecords(userId: string, startDate?: string, endDate?: string): Promise<PrayerRecord[]>;
  createPrayerRecord(record: InsertPrayerRecord): Promise<PrayerRecord>;
  updatePrayerRecord(userId: string, date: string, prayers: any): Promise<PrayerRecord>;
  batchUpdatePrayerRecords(userId: string, updates: Array<{ date: string; prayers: DailyPrayers }>): Promise<PrayerRecord[]>;

  // Achievements
  getAchievements(userId: string): Promise<Achievement[]>;
  createAchievement(achievement: InsertAchievement): Promise<Achievement>;

  // User statistics
  getUserStats(userId: string): Promise<UserStats | undefined>;
  createUserStats(stats: InsertUserStats): Promise<UserStats>;
  updateUserStats(userId: string, updates: Partial<UserStats>): Promise<UserStats>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private prayerRecords: Map<string, PrayerRecord>; // key: userId-date
  private achievements: Map<string, Achievement>;
  private userStats: Map<string, UserStats>; // key: userId

  constructor() {
    this.users = new Map();
    this.prayerRecords = new Map();
    this.achievements = new Map();
    this.userStats = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const existingUser = this.users.get(userData.id!);
    if (existingUser) {
      const updatedUser: User = {
        ...existingUser,
        ...userData,
        updatedAt: new Date(),
      };
      this.users.set(userData.id!, updatedUser);
      return updatedUser;
    }

    const user: User = {
      id: userData.id!,
      username: userData.username ?? null,
      email: userData.email ?? null,
      passwordHash: userData.passwordHash || '', // Required for custom auth
      firstName: userData.firstName ?? null,
      lastName: userData.lastName ?? null,
      profileImageUrl: userData.profileImageUrl ?? null,
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(user.id, user);

    // Initialize user stats
    const userStatsData: UserStats = {
      id: randomUUID(),
      userId: user.id,
      totalPrayers: 0,
      onTimePrayers: 0,
      qazaPrayers: 0,
      currentStreak: 0,
      bestStreak: 0,
      perfectWeeks: 0,
      lastStreakUpdate: null,
      updatedAt: new Date(),
    };
    this.userStats.set(user.id, userStatsData);

    return user;
  }

  async getPrayerRecord(userId: string, date: string): Promise<PrayerRecord | undefined> {
    return this.prayerRecords.get(`${userId}-${date}`);
  }

  async getPrayerRecords(userId: string, startDate?: string, endDate?: string): Promise<PrayerRecord[]> {
    const records = Array.from(this.prayerRecords.values())
      .filter(record => record.userId === userId);

    if (startDate && endDate) {
      return records.filter(record => 
        record.date >= startDate && record.date <= endDate
      );
    }

    return records;
  }

  async createPrayerRecord(insertRecord: InsertPrayerRecord): Promise<PrayerRecord> {
    const id = randomUUID();
    const userId = insertRecord.userId || "demo-user"; // Ensure userId is never null
    const record: PrayerRecord = {
      id,
      userId,
      date: insertRecord.date,
      prayers: insertRecord.prayers as PrayerRecord["prayers"],
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    this.prayerRecords.set(`${record.userId}-${record.date}`, record);
    return record;
  }

  async updatePrayerRecord(userId: string, date: string, prayers: PrayerRecord["prayers"]): Promise<PrayerRecord> {
    const key = `${userId}-${date}`;
    const existing = this.prayerRecords.get(key);

    if (existing) {
      const updated: PrayerRecord = {
        ...existing,
        prayers,
        updatedAt: new Date(),
      };
      this.prayerRecords.set(key, updated);
      return updated;
    }

    // Create new record if it doesn't exist
    return this.createPrayerRecord({ userId, date, prayers });
  }

  async batchUpdatePrayerRecords(userId: string, updates: Array<{ date: string; prayers: DailyPrayers }>): Promise<PrayerRecord[]> {
    const updatedRecords: PrayerRecord[] = [];

    for (const update of updates) {
      const record = await this.updatePrayerRecord(userId, update.date, update.prayers as PrayerRecord["prayers"]);
      updatedRecords.push(record);
    }

    return updatedRecords;
  }

  async getAchievements(userId: string): Promise<Achievement[]> {
    return Array.from(this.achievements.values())
      .filter(achievement => achievement.userId === userId)
      .sort((a, b) => new Date(b.earnedDate).getTime() - new Date(a.earnedDate).getTime());
  }

  async createAchievement(insertAchievement: InsertAchievement): Promise<Achievement> {
    const userId = insertAchievement.userId || "demo-user"; // Ensure userId is never null

    // Check for existing achievement with same userId, type, and earnedDate (idempotency)
    const existingAchievement = Array.from(this.achievements.values()).find(
      achievement => 
        achievement.userId === userId &&
        achievement.type === insertAchievement.type &&
        achievement.earnedDate === insertAchievement.earnedDate
    );

    if (existingAchievement) {
      return existingAchievement; // Return existing achievement instead of creating duplicate
    }

    const id = randomUUID();
    const achievement: Achievement = {
      id,
      userId,
      type: insertAchievement.type,
      title: insertAchievement.title,
      description: insertAchievement.description,
      earnedDate: insertAchievement.earnedDate,
      metadata: insertAchievement.metadata as Achievement["metadata"] || null,
      createdAt: new Date(),
    };

    this.achievements.set(id, achievement);
    return achievement;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(user: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User> {
    const userData = {
      ...user,
      id: randomUUID(),
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    this.users.set(userData.id, userData);

    // Initialize user stats
    const userStatsData: UserStats = {
      id: randomUUID(),
      userId: userData.id,
      totalPrayers: 0,
      onTimePrayers: 0,
      qazaPrayers: 0,
      currentStreak: 0,
      bestStreak: 0,
      perfectWeeks: 0,
      lastStreakUpdate: null,
      updatedAt: new Date(),
    };
    this.userStats.set(userData.id, userStatsData);

    return userData;
  }

  async getUserStats(userId: string): Promise<UserStats | undefined> {
    return this.userStats.get(userId);
  }

  async createUserStats(insertStats: InsertUserStats): Promise<UserStats> {
    const id = randomUUID();
    const stats: UserStats = {
      ...insertStats,
      id,
      userId: insertStats.userId || null,
      totalPrayers: insertStats.totalPrayers || 0,
      onTimePrayers: insertStats.onTimePrayers || 0,
      qazaPrayers: insertStats.qazaPrayers || 0,
      currentStreak: insertStats.currentStreak || 0,
      bestStreak: insertStats.bestStreak || 0,
      perfectWeeks: insertStats.perfectWeeks || 0,
      lastStreakUpdate: insertStats.lastStreakUpdate || null,
      updatedAt: new Date(),
    };

    this.userStats.set(stats.userId!, stats);
    return stats;
  }

  async updateUserStats(userId: string, updates: Partial<UserStats>): Promise<UserStats> {
    const existing = this.userStats.get(userId);
    if (!existing) {
      throw new Error("User stats not found");
    }

    const updated: UserStats = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };

    this.userStats.set(userId, updated);
    return updated;
  }
}

// Database storage implementation
export class DatabaseStorage implements IStorage {
  // User operations (IMPORTANT: these are mandatory for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values({
        ...userData,
        createdAt: new Date(),
        updatedAt: new Date(),
      })
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();

    // Initialize user stats if it's a new user
    const existingStats = await this.getUserStats(user.id);
    if (!existingStats) {
      await this.createUserStats({ userId: user.id });
    }

    return user;
  }

  // Prayer records
  async getPrayerRecord(userId: string, date: string): Promise<PrayerRecord | undefined> {
    const [record] = await db
      .select()
      .from(prayerRecords)
      .where(and(
        eq(prayerRecords.userId, userId),
        eq(prayerRecords.date, date)
      ));
    return record;
  }

  async getPrayerRecords(userId: string, startDate?: string, endDate?: string): Promise<PrayerRecord[]> {
    const conditions = [eq(prayerRecords.userId, userId)];

    if (startDate && endDate) {
      conditions.push(
        gte(prayerRecords.date, startDate),
        lte(prayerRecords.date, endDate)
      );
    }

    return await db
      .select()
      .from(prayerRecords)
      .where(and(...conditions));
  }

  async createPrayerRecord(record: InsertPrayerRecord): Promise<PrayerRecord> {
    const [created] = await db
      .insert(prayerRecords)
      .values([record as any])
      .returning();
    return created;
  }

  async updatePrayerRecord(userId: string, date: string, prayers: PrayerRecord["prayers"]): Promise<PrayerRecord> {
    const existing = await this.getPrayerRecord(userId, date);

    if (existing) {
      const [updated] = await db
        .update(prayerRecords)
        .set({
          prayers,
          updatedAt: new Date(),
        })
        .where(eq(prayerRecords.id, existing.id))
        .returning();
      return updated;
    }

    return this.createPrayerRecord({ 
      userId, 
      date, 
      prayers: prayers as PrayerRecord["prayers"]
    });
  }

  async batchUpdatePrayerRecords(userId: string, updates: Array<{ date: string; prayers: DailyPrayers }>): Promise<PrayerRecord[]> {
    const updatedRecords: PrayerRecord[] = [];

    // Process updates sequentially to maintain data consistency
    for (const update of updates) {
      const record = await this.updatePrayerRecord(userId, update.date, update.prayers as PrayerRecord["prayers"]);
      updatedRecords.push(record);
    }

    return updatedRecords;
  }

  // Achievements
  async getAchievements(userId: string): Promise<Achievement[]> {
    return await db
      .select()
      .from(achievements)
      .where(eq(achievements.userId, userId));
  }

  async createAchievement(achievement: InsertAchievement): Promise<Achievement> {
    // Check for existing achievement with same userId, type, and earnedDate (idempotency)
    const [existingAchievement] = await db
      .select()
      .from(achievements)
      .where(and(
        eq(achievements.userId, achievement.userId!),
        eq(achievements.type, achievement.type),
        eq(achievements.earnedDate, achievement.earnedDate)
      ));

    if (existingAchievement) {
      return existingAchievement; // Return existing achievement instead of creating duplicate
    }

    const [created] = await db
      .insert(achievements)
      .values([achievement as any])
      .returning();
    return created;
  }

  // User statistics
  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(user: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User> {
    const [created] = await db
      .insert(users)
      .values(user as any)
      .returning();

    // Initialize user stats
    await this.createUserStats({ userId: created.id });

    return created;
  }

  async getUserStats(userId: string): Promise<UserStats | undefined> {
    const [stats] = await db
      .select()
      .from(userStats)
      .where(eq(userStats.userId, userId));
    return stats;
  }

  async createUserStats(stats: InsertUserStats): Promise<UserStats> {
    const [created] = await db
      .insert(userStats)
      .values([stats])
      .returning();
    return created;
  }

  async updateUserStats(userId: string, updates: Partial<UserStats>): Promise<UserStats> {
    const [updated] = await db
      .update(userStats)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(userStats.userId, userId))
      .returning();

    if (!updated) {
      throw new Error("User stats not found");
    }

    return updated;
  }
}

// Use database storage for persistent data, fallback to memory storage
export const storage = db ? new DatabaseStorage() : new MemStorage();

