  --sidebar: hsl(222.2, 84%, 4.9%, 0.7);
  --sidebar-foreground: hsl(210, 40%, 98%);
  --sidebar-primary: hsl(158, 60%, 30%);
  --sidebar-primary-foreground: hsl(210, 40%, 98%);
  --sidebar-accent: hsl(217.2, 32.6%, 17.5%);
